      array = new Uint8Array(buffer);
      midiFile = MidiFile(array);
      console.log(midiFile);
      player = MidiPlayer(midiFile);
      _ref1 = Track(), playNote = _ref1.playNote, releaseNote = _ref1.releaseNote;
      meta = {};
      handleEvent = function(event, state) {
        var deltaTime, noteNumber, subtype, time, type, velocity;
        time = state.time;
