piano.content:
(function() {
  var TouchCanvas;

  TouchCanvas = require("touch-canvas");

  module.exports = function() {
    var canvas, ids, onNotes, range, self;
    range = 24;
    onNotes = [];
    ids = [];
    canvas = TouchCanvas();
    canvas.on("touch", function(p) {
      var note;
      note = Math.floor(p.x * range);
      return self.playNote(note, p.identifier);
    });
    canvas.on("release", function(p) {
      return self.releaseNote(p.identifier);
    });
    return self = {
      releaseNote: function(identifier) {
        return onNotes[ids[identifier]] = false;
      },
      playNote: function(note, identifier) {
        onNotes[note] = true;
        return ids[identifier] = note;
      },
      element: function() {
        return canvas.element();
      },
      draw: function() {
        var height, n, width, _i, _results;
        n = range;
        canvas.clear();
        width = canvas.width() / n;
        height = canvas.height();
        return (function() {
          _results = [];
          for (var _i = 0; 0 <= n ? _i < n : _i > n; 0 <= n ? _i++ : _i--){ _results.push(_i); }
          return _results;
        }).apply(this).forEach(function(n) {
          var hue, lightness, saturation;
          hue = (n % 12) * 360 / 12;
          saturation = "75%";
          lightness = "50%";
          if (onNotes[n]) {
            lightness = "75%";
          }
          return canvas.drawRect({
            x: width * n,
            y: 0,
            width: width,
            height: height,
            color: "hsl(" + hue + ", " + saturation + ", " + lightness + ")"
          });
        });
      }
    };
  };

}).call(this);

