(function() {
  var constantOneCurve, pulseCurve, _i, _results;

  pulseCurve = new Float32Array(256);

  (function() {
    _results = [];
    for (_i = 0; _i < 256; _i++){ _results.push(_i); }
    return _results;
  }).apply(this).forEach(function(i) {
    if (i < 128) {
      return pulseCurve[i] = -1;
    } else {
      return pulseCurve[i] = 1;
    }
  });

  constantOneCurve = new Float32Array(2);

  constantOneCurve[0] = 1;

  constantOneCurve[1] = 1;

  module.exports = function(context) {
    var constantOneShaper, node, pulseShaper, widthGain;
    node = context.createOscillator();
    node.type = "sawtooth";
    node.start();
    pulseShaper = context.createWaveShaper();
    pulseShaper.curve = pulseCurve;
    node.connect(pulseShaper);
    widthGain = context.createGain();
    widthGain.gain.value = 0.0;
    node.width = widthGain.gain;
    widthGain.connect(pulseShaper);
    constantOneShaper = context.createWaveShaper();
    constantOneShaper.curve = constantOneCurve;
    node.connect(constantOneShaper);
    constantOneShaper.connect(widthGain);
    node.connect = function() {
      return pulseShaper.connect.apply(pulseShaper, arguments);
    };
    node.disconnect = function() {
      return pulseShaper.disconnect.apply(pulseShaper, arguments);
    };
    return node;
  };

}).call(this);

